import React from "react";
import PropTypes from "prop-types";
import Loading from "../Loding";
import { couldStartTrivia } from "typescript";
import styled from "styled-components";
const Block = styled.div`
  margin-bottom: 30px;
`;
let exArray=[];

const Exs=({ex})=>{
  var newDesc;
  var linksss;
    if(ex.description){
      newDesc=ex.description
      if(newDesc.length>20){
        newDesc=ex.description.slice(0,30);
        newDesc=newDesc+"..."
      }
    }
    if(ex.links?.website){
      linksss=ex.links?.website;
    }else{
      linksss="";
    }

    return(
      <Block>
      <h2>{ex.name}</h2>
      <div>{newDesc}</div>
      <a href={links}>{linksss}</a>
      </Block>
    )


}
const Make_ex=(exchanges)=>{
  exchanges.map((ex)=>{
      const exex=<Exs key={ex.id} ex={ex}/>
      exArray.push(exex);
  }
  )
}

const ExchangePresenter = ({ exchange, loading }) => {

  if (loading) {
    return <Loading />;
  } else {
    Make_ex(exchange)
    return(<>{exArray}</>);
  }
};

export default ExchangePresenter;
