import React from "react";
import PropTypes from "prop-types";
import Loading from "../Loding";

let exArray=[];

const Exs=(ex)=>{
  const newDes=ex.description.slice(0,20);
  return(
    <>
    <h1>{ex.name}</h1>
    <div>{newDes}</div>
    <div>{ex.links.website} </div>
    </>
  )
}
const Make_ex=(exchanges)=>{
  exchanges.map((ex)=>{
      const exex=<Exs ex={ex}/>
      exArray.push(exex);
  }
  )
}

const ExchangePresenter = ({ exchange, loading }) => {
  if (loading) {
    return <Loading />;
  } else {
      return exchange.map((ex) => 
      (
      <div key={ex.id}>
        <div className="font-weight: 800;">
          {ex.name} 
        </div>
        <div>
          {ex.description} 
        </div>
        <div>
          {ex.links.website} 
        </div>
      </div>
    ));
  }
};

export default ExchangePresenter;
