import React from "react";
import PropTypes from "prop-types";
import Loading from "../Loding";
import { couldStartTrivia } from "typescript";

let exArray=[];

const Exs=({ex})=>{
  var newDesc;
  var linksss;
    if(ex.description){
      if(newDesc && newDesc.length>20){
        newDesc=ex.description.slice(0,30);
        newDesc=newDesc+"..."
      }else{
        newDesc=ex.description
      }
    }
    if(ex.links?.website){
      linksss=ex.links?.website;
    }else{
      linksss="";
    }

    return(
      <>
      <h2>{ex.name}</h2>
      <div>{newDesc}</div>
      <div>{linksss}</div>
      </>
    )


}
const Make_ex=(exchanges)=>{
  exchanges.map((ex)=>{
      const exex=<Exs key={ex.id} ex={ex}/>
      exArray.push(exex);
  }
  )
}

const ExchangePresenter = ({ exchange, loading }) => {

  if (loading) {
    return <Loading />;
  } else {
    Make_ex(exchange)
    return(<div>{exArray}</div>);
  }
};

export default ExchangePresenter;
